INSERT INTO system_template (id,title,comment,image,content,template_type) VALUES   (80,    'TemplateTeam',         '',     'woman_white.png',       '<title></title>\r\n<link href=\"{WEB_PATH}main/css/dokeos2_black_tablet/templates.css\" rel=\"stylesheet\" type=\"text/css\" />\r\n<style type=\"text/css\">\r\n#page_template1 {\r\n margin:auto;\r\n font-size: 12px;\r\n }\r\n\r\n.margin_template {\r\n \r\n margin-left:12px;\r\n \r\n width:auto;\r\n \r\n } \r\n\r\n.padding_template {\r\n \r\n padding-left:10px;\r\n padding-top:10px;\r\n padding-right:5px;\r\n padding-bottom:10px;\r\n \r\n }</style>\r\n<div id=\"page_template1\">\r\n <table align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"756px\">\r\n <tbody>\r\n <tr>\r\n <td align=\"left\" scope=\"row\">\r\n <h2 style=\"margin-left: 8px\">\r\n <strong>&nbsp;&nbsp; TEAM</strong></h2>\r\n </td>\r\n </tr>\r\n <tr>\r\n <th scope=\"row\">\r\n <div class=\"box_template\">\r\n <table align=\"center\" cellpadding=\"0\" cellspacing=\"10\" style=\"height: 96px; width: 100%\">\r\n <tbody>\r\n <tr>\r\n <td align=\"left\" class=\"padding_template\" scope=\"row\">\r\n <h2>\r\n <strong>Heads</strong></h2>\r\n <p>\r\n Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolores magna aliqua.</p>\r\n </td>\r\n <td width=\"80\">\r\n <img align=\"right\" border=\"0\" height=\"81\" hspace=\"0\" alt=\"supervisor\" title=\"supervisor\" src=\"{WEB_PATH}main/default_course_document/images/icons/office/supervisor.png\" vspace=\"0\" width=\"81\" /></td>\r\n </tr>\r\n </tbody>\r\n </table>\r\n </div>\r\n </th>\r\n </tr>\r\n <tr>\r\n <th scope=\"row\">\r\n <div class=\"box_template\">\r\n <table align=\"center\" cellpadding=\"0\" cellspacing=\"10\" style=\"height: 69px; width: 100%\" width=\"100%\">\r\n <tbody>\r\n <tr>\r\n <th align=\"left\" scope=\"row\" width=\"12%\">\r\n <img align=\"right\" border=\"0\" height=\"81\" hspace=\"0\" alt=\"admin\" title=\"admin\" src=\"{WEB_PATH}main/default_course_document/images/icons/office/admin.png\" vspace=\"0\" width=\"81\" /></th>\r\n <td align=\"left\" class=\"padding_template\" scope=\"row\">\r\n <h2>\r\n <strong>Administration</strong></h2>\r\n <p>\r\n Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</p>\r\n </td>\r\n </tr>\r\n </tbody>\r\n </table>\r\n </div>\r\n </th>\r\n </tr>\r\n <tr>\r\n <th scope=\"row\">\r\n <div class=\"box_template\">\r\n <table align=\"center\" cellpadding=\"0\" cellspacing=\"10\" style=\"height: 58px; width: 100%\" width=\"100%\">\r\n <tbody>\r\n <tr>\r\n <td align=\"left\" class=\"padding_template\" scope=\"row\">\r\n <h2>\r\n <strong>IT</strong></h2>\r\n <p>\r\n Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum. Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</p>\r\n </td>\r\n <td width=\"80\">\r\n <img align=\"right\" border=\"0\" height=\"81\" hspace=\"0\" alt=\"sessions admin\" title=\"sessions admin\" src=\"{WEB_PATH}main/default_course_document/images/icons/office/sessions_admin.png\" vspace=\"0\" width=\"81\" /></td>\r\n </tr>\r\n </tbody>\r\n </table>\r\n </div>\r\n </th>\r\n </tr>\r\n <tr>\r\n <th scope=\"row\">\r\n <div class=\"box_template\">\r\n <table align=\"center\" cellpadding=\"0\" cellspacing=\"10\" style=\"height: 55px; width: 100%\" width=\"100%\">\r\n <tbody>\r\n <tr>\r\n <th align=\"left\" scope=\"row\" width=\"12%\">\r\n <img align=\"right\" border=\"0\" height=\"81\" hspace=\"0\" alt=\"trainer\" title=\"trainer\" src=\"{WEB_PATH}main/default_course_document/images/icons/office/trainer.png\" vspace=\"0\" width=\"81\" /></th>\r\n <td align=\"left\" class=\"padding_template\" scope=\"row\">\r\n <h2>\r\n <strong>Trainers</strong></h2>\r\n <p>\r\n Sed ut perspiciatis unde omnis iste natus error sit voluptatem accusantium doloremque laudantium, totam rem aperiam, eaque ipsa quae ab illo inventore.</p>\r\n </td>\r\n </tr>\r\n </tbody>\r\n </table>\r\n </div>\r\n </th>\r\n </tr>\r\n <tr>\r\n <th scope=\"row\">\r\n <div class=\"box_template\">\r\n <table align=\"center\" cellpadding=\"0\" cellspacing=\"10\" style=\"height: 58px; width: 100%\" width=\"100%\">\r\n <tbody>\r\n <tr>\r\n <td align=\"left\" class=\"padding_template\" scope=\"row\">\r\n <h2>\r\n <strong>Tutors</strong></h2>\r\n <p>\r\n Nemo enim ipsam voluptatem quia voluptas sit aspernatur aut odit aut fugit, sed quia consequuntur magni dolores eos qui ratione voluptatem sequi nesciunt.</p>\r\n </td>\r\n <td width=\"80\">\r\n <img align=\"right\" border=\"0\" height=\"81\" hspace=\"0\" alt=\"tutor\" title=\"tutor\" src=\"{WEB_PATH}main/default_course_document/images/icons/office/tutor.png\" vspace=\"0\" width=\"81\" /></td>\r\n </tr>\r\n </tbody>\r\n </table>\r\n </div>\r\n </th>\r\n </tr>\r\n </tbody>\r\n </table>\r\n</div>\r\n<!-- end table for the cells of content --><!-- end white table for the course -->','home');
